    public void nextMessage (View view){
        TextView edit = (TextView) findViewById(R.id.textBox);

        Button btnAnser = findViewById(R.id.btnShowAnswer);
        btnAnser.setVisibility(View.GONE);

        switch (next){

            case "A":

                if (!letters.isEmpty()) {

                    showNeverhave();
                    next = "B";
                    Log.d("State", next);

                    break;
                }

            case "B":

                if(!pictureDays.isEmpty()) {

                    showPictureDay();

                    next = "C";
                    order++;
                    Log.d("State", next);
                    break;

                }
            case "C":
                if (!mostLikely.isEmpty()){

                    showMostLikely();
                    next = "A";
                    Log.d("State", next);
                    break;
                }

                else if (!letters.isEmpty()) {
                showNeverhave();
                Log.d("State", "elseeeeee: ");
                next = "B";
                break;
            }


                default:

                    edit.setText("Börjar om!");
                    Log.d("State", "defaultttttttttttttttt: ");
                    addNeverHaveiEver();
                    addMostLikely();
                    addPictureDay();


                    next = "A";
                    break;
        }

    }

    public void showAnswer (View view){
        TextView edit = (TextView) findViewById(R.id.textBox);
        Button btnAnswer = findViewById(R.id.btnShowAnswer);
        pictureDay tempPic = new pictureDay("", "", "", "");
        ImageView image = findViewById(R.id.pictureDayImg);


        for (pictureDay pic: pictureDays) {

            if (edit.getText().toString().equals(pic.question)){
                edit.setText(pic.answer);
                tempPic = pic;
            }
        }
        btnAnswer.setVisibility(View.GONE);
        Button btnNext = findViewById(R.id.button);
        btnNext.setVisibility(View.VISIBLE);
        Picasso.get().load(tempPic.url2).into(image);
        pictureDays.remove(tempPic);





    }

    private void showPictureDay() {
        TextView edit = (TextView) findViewById(R.id.textBox);
        Random rand = new Random();
        ImageView image = findViewById(R.id.pictureDayImg);
        ImageView scott = findViewById(R.id.imageView);
        ImageView mostlikely = findViewById(R.id.imageView_mostlikely);

        scott.setVisibility(View.GONE);
        mostlikely.setVisibility(View.GONE);

        image.setVisibility(View.VISIBLE);

        int randNr = rand.nextInt(pictureDays.size());
        pictureDay tempPic = pictureDays.get(randNr);
        Picasso.get().load(tempPic.url).into(image);
        edit.setBackgroundColor(Color.parseColor("#75FF33"));

        edit.setText(tempPic.question);

        Button btnAnswer = findViewById(R.id.btnShowAnswer);
        Button btnNext = findViewById(R.id.button);
        btnNext.setVisibility(View.GONE);
        btnAnswer.setVisibility(View.VISIBLE);




    }

    private void showNeverhave() {
        TextView edit = (TextView) findViewById(R.id.textBox);
        Random rand = new Random();
        ImageView image = findViewById(R.id.pictureDayImg);
        ImageView scott = findViewById(R.id.imageView);
        ImageView mostlikely = findViewById(R.id.imageView_mostlikely);



        int randNr = rand.nextInt(letters.size());

        mostlikely.setVisibility(View.GONE);
        image.setVisibility(View.GONE);

        scott.setVisibility(View.VISIBLE);
        edit.setBackgroundColor(Color.parseColor("#DBFF33"));

        edit.setText("Jag har aldrig " + letters.get(randNr));
        letters.remove(randNr);
    }

    private void showMostLikely() {
        TextView edit = (TextView) findViewById(R.id.textBox);
        Random rand = new Random();
        ImageView image = findViewById(R.id.pictureDayImg);
        ImageView scott = findViewById(R.id.imageView);
        ImageView mostlikely = findViewById(R.id.imageView_mostlikely);

        scott.setVisibility(View.GONE);
        image.setVisibility(View.GONE);
        mostlikely.setVisibility(View.VISIBLE);



        int randNr = rand.nextInt(mostLikely.size());

        edit.setBackgroundColor(Color.parseColor("#FF5733"));
        edit.setText("Vem är mest trolig att " + mostLikely.get(randNr));
        mostLikely.remove(randNr);
    }
